mapper.select:
select * from installation_package


        and id = #{id}


        and name = #{name}


        and major_version_number = #{majorVersionNumber}


        and minor_version_number = #{minorVersionNumber}


        and revision_version_number = #{revisionVersionNumber}


        and state = #{state}


        and operating_state = #{operatingState}


        and page_tag_info_id = #{pageTagInfoId}

mapper.update:
update installation_package
installation_package=#{installationPackage}, name=#{name}, major_version_number=#{majorVersionNumber}, minor_version_number=#{minorVersionNumber}, revision_version_number=#{revisionVersionNumber}, state=#{state}, operating_state=#{operatingState}, page_tag_info=#{pageTagInfoId}, update_time = UNIX_TIMESTAMP(now())
where id=#{id}
